<!-- Contact Form -->
<section class="container contact-form">
    <div class="form-container">
        <h1>Get in touch <span>Feel free to leave your feedback.</span></h1>
        <form action="">
            <div class="inputs">
                <input type="text" name="name" placeholder="John Doe">
                <input type="mail" name="email" placeholder="john.doe@gmail.com">
                <input type="text" name="subject" placeholder="Subject">
                <textarea name="feedback" cols="30" rows="10" placeholder="Feedback ..."></textarea>
            </div>
            <div class="submit">
                <input type="submit" name="submit" class="btn btn-primary">
            </div>
        </form>
    </div>
</section>